mapper.select:
SELECT * FROM ( SELECT TMP_PAGE.*, ROWNUM PAGEHELPER_ROW_ID FROM (
select DAXXID,DAXX from WTGL_DACC

    and WTMC like '%' || #{WTMC} || '%'

    and WTLB = #{WTLB}
    and WTXL = #{WTXL}
    and GXH = #{GXH}
    and CPXH = #{CPXH}
    and JH = #{JH}
    and DAXXID not in (

            #{id}

        )


        and (

            (WTMC like '%' || #{gjz} || '%'
            or WTMS like '%' || #{gjz} || '%'
            or DAXX like '%' || #{gjz} || '%')

        )


    and RD = (
    select MAX(RD) from WTGL_DACC

        and WTMC like '%' || #{WTMC} || '%'

        and WTLB = #{WTLB}
        and WTXL = #{WTXL}
        and GXH = #{GXH}
        and CPXH = #{CPXH}
        and JH = #{JH}
        and DAXXID not in (

                #{id}

            )


            and (

                (WTMC like '%' || #{gjz} || '%'
                or WTMS like '%' || #{gjz} || '%'
                or DAXX like '%' || #{gjz} || '%')

            )


    )

) TMP_PAGE) WHERE PAGEHELPER_ROW_ID = 1
